async function dnsQuery(domain, type) {
  try {
    const res = await this.helpers.httpRequest({
      method: 'GET',
      url: `https://dns.google/resolve?name=${domain}&type=${type}`,
      json: true,
    });

    if (!res.Answer) return [];
    return res.Answer.map(r => r.data);
  } catch (e) {
    return [];
  }
}

function normalizeDomain(value) {
  if (!value) return "";

  try {
    if (value.startsWith("http")) return new URL(value).hostname;
    return value.trim();
  } catch {
    return value;
  }
}

async function enrich(domain) {
  return {
    indicator: domain,
    dns: {
      A: await dnsQuery(domain, 'A'),
      NS: await dnsQuery(domain, 'NS'),
      MX: await dnsQuery(domain, 'MX'),
      TXT: await dnsQuery(domain, 'TXT'),
    },
    whois: await fetchWhois(domain),
  };
}


// -------- MAIN PER-ITEM EXECUTION --------

const item = $input.item;

// domain comes from item.json.data
const domain = normalizeDomain(item.json?.domain || "");

const enrichment = await enrich(domain);

// ALWAYS return { json: {...object...} }
return {
  json: {
    original: item.json || {},
    enrichment: enrichment || {},
  }
};

async function fetchWhois(domain) {
  const apiKey = 'DjPEOQrHsPhSneJ+ttdlyw==XPvK2p6cNCkxZ2lS';

  try {
    return await this.helpers.httpRequest({
      method: 'GET',
      url: 'https://api.api-ninjas.com/v1/whois',
      qs: { domain },
      headers: { 'X-Api-Key': apiKey },
      json: true,
      timeout: 10000,
    });
  } catch {
    return { error: 'whois_lookup_failed' };
  }
}

function normalizeDomain(value) {
  if (!value || typeof value !== 'string') return '';
  try {
    if (value.startsWith('http')) return new URL(value).hostname;
    return value.trim();
  } catch {
    return value.trim();
  }
}

// ---------------- MAIN ----------------

const alert = $input.item.json;

// Extract domain artifacts
const domainArtifacts = Array.isArray(alert.artifacts)
  ? alert.artifacts.filter(a => a.dataType === 'domain' && a.data)
  : [];

if (domainArtifacts.length === 0) {
  return {
    json: {
      ...alert,
      whois_enrichment: [],
      enrichment_status: 'no_domain_artifacts',
    },
  };
}

// WHOIS enrichment for each domain
const whoisResults = [];

for (const artifact of domainArtifacts) {
  const domain = normalizeDomain(artifact.data);

  if (!domain) continue;

  const whois = await fetchWhois(domain);

  whoisResults.push({
    indicator: domain,
    tags: artifact.tags || [],
    whois,
  });
}

return {
  json: {
    ...alert,
    whois_enrichment: whoisResults,
    enrichment_status: 'success',
  },
};
